import React, { useRef } from 'react';
import styled from 'styled-components';

<style >

const Container = styled.div`
  height: 100vh;
  overflow-y: scroll;
  scroll-behavior: smooth;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
`;

const Section = styled.section`
  margin: 40px 0;
`;

const ImageLink = styled.a`
  display: block;
  margin: 20px 0;
`;

const AuthLinks = styled.div`
  margin-top: 20px;
`;

</style>


const Default = () => {
  const section1Ref = useRef(null);
  const section2Ref = useRef(null);

  const scrollToSection = (ref) => {
    if (ref && ref.current) {
      ref.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <Container>
      <h1>Welcome to Task Manager</h1>
      <p>Manage your tasks efficiently with our easy-to-use Task Manager.</p>

      <button onClick={() => scrollToSection(section1Ref)}>Scroll to Section 1</button>
      <button onClick={() => scrollToSection(section2Ref)}>Scroll to Section 2</button>

      <AuthLinks>
        <a href="/login">Login</a> | <a href="/signup">Sign Up</a>
      </AuthLinks>

      <Section ref={section1Ref}>
        <h2>Section 1</h2>
        <p>Content for Section 1 goes here.</p>
        <ImageLink href="https://unsplash.com/photos/hXpPoH1LCl4" target="_blank">
          <img src="https://unsplash.com/photos/hXpPoH1LCl4" alt="Image 1" />
        </ImageLink>
      </Section>

      <Section ref={section2Ref}>
        <h2>Section 2</h2>
        <p>Content for Section 2 goes here.</p>
        <ImageLink href="https://unsplash.com/photos/gUIJ0YszPig" target="_blank">
          <img src="https://unsplash.com/photos/gUIJ0YszPig" alt="Image 2" />
        </ImageLink>
      </Section>
    </Container>
  );
};

export default Default;
