import React, { useRef, useEffect, useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  height: 100vh;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
`;

const WelcomeText = styled.h1`
  margin-top: 50px;
  font-size: 24px;
  font-weight: bold;
  text-align: center;
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 20px;
  margin-top: 20px;
`;

const HighlightedButton = styled.button`
  background-color: #f0f0f0;
  border: none;
  padding: 10px 20px;
  font-weight: bold;
  cursor: pointer;
`;

const Slider = styled.div`
  display: flex;
  transition: transform 1s ease-in-out;
`;

const Slide = styled.div`
  min-width: 100%;
`;

const Image = styled.img`
  width: 100%;
  height: auto;
`;

const Default = () => {
  const sliderRef = useRef(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const imageUrls = [
    'https://images.unsplash.com/photo-1555685812-4b943f1cb0eb',
    'https://images.unsplash.com/photo-1555685812-4b943f1cb0eb',
    'https://images.unsplash.com/photo-1555685812-4b943f1cb0eb',
    'https://images.unsplash.com/photo-1555685812-4b943f1cb0eb',
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) =>
        prevIndex === imageUrls.length - 1 ? 0 : prevIndex + 1
      );
    }, 5000); // Change image every 5 seconds

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    if (sliderRef.current) {
      sliderRef.current.style.transform = `translateX(-${currentIndex * 100}%)`;
    }
  }, [currentIndex]);

  return (
    <Container>
      <WelcomeText>Welcome to Task Manager</WelcomeText>
      <ButtonContainer>
        <HighlightedButton>Login</HighlightedButton>
        <HighlightedButton>Sign Up</HighlightedButton>
      </ButtonContainer>
      <Slider ref={sliderRef}>
        {imageUrls.map((imageUrl, index) => (
          <Slide key={index}>
            <Image src={imageUrl} alt={`Image ${index + 1}`} />
          </Slide>
        ))}
      </Slider>
    </Container>
  );
};

export default Default;
