import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import Dashboard from '.components/Dashboard'; // Import your Dashboard component

const Navbar = () => {
  const [name, setName] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Track user login status
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false); // Set login status to false on logout
    navigate('/login');
  };

  const getUsername = async () => {
    const response = await fetch(`http://localhost:80/auth/getUser`, {
      method: 'POST',
      headers: {
        'auth-token': localStorage.getItem('token'),
      },
    });

    const json = await response.json();
    setName(json.name);
  };

  useEffect(() => {
    if (localStorage.getItem('token')) {
      getUsername();
      setIsLoggedIn(true); // Set login status to true if the user is logged in
    }
  }, []);

  return (
    <>
      <AppBar position="static" color="primary">
        <Toolbar>
          <Typography variant="h6" style={{ flexGrow: 1 }}>
            <Link to="/" style={{ color: 'white', textDecoration: 'none' }}>
              Task Manager
            </Link>
          </Typography>
          <Button color="inherit">
            <Link to="/" style={{ color: 'white', textDecoration: 'none' }}>
              Home
            </Link>
          </Button>
          {isLoggedIn ? (
            <>
              <Button color="inherit">
                <Link
                  to="/"
                  style={{ color: 'white', textDecoration: 'none' }}
                >
                  {name}
                </Link>
              </Button>
              <Button color="inherit" onClick={handleLogout}>
                Log out
              </Button>
            </>
          ) : (
            <>
              <Button color="inherit">
                <Link
                  to="/login"
                  style={{ color: 'white', textDecoration: 'none' }}
                >
                  Login
                </Link>
              </Button>
              <Button color="inherit">
                <Link
                  to="/signup"
                  style={{ color: 'white', textDecoration: 'none' }}
                >
                  Sign up
                </Link>
              </Button>
            </>
          )}
        </Toolbar>
      </AppBar>
      {isLoggedIn && <Dashboard />} {/* Render Dashboard if user is logged in */}
    </>
  );
};

export default Navbar;
