import './App.css';
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Home from './components/Home';
import ListState from './context/lists/ListState';
import CreateList from './components/CreateList';
import UpdateList from './components/UpdateList';
import Tasks from './components/Tasks';
import TaskState from './context/tasks/TaskState';
import Login from './components/Login';
import Signup from './components/Signup';
import UpdateTask from './components/UpdateTask';
import Cal from './components/Cal';
import Dashboard from './components/Dashboard'; // Import your dashboard component

function App() {
  return (
    <TaskState>
      <ListState>
        <Router>
          <Routes>
            <Route path="/" element={<Dashboard />} /> {/* Set dashboard as the initial page */}
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup />} />
            <Route path="/home" element={<Home />} /> {/* Example: Add a Home route */}
            <Route path="/createList" element={<CreateList />} />
            <Route path="/updateList/:id" element={<UpdateList />} />
            <Route path="/:list_id/tasks" element={<Tasks />} />
            <Route path="/:list_id/updateTask/:task_id" element={<UpdateTask/>} />
            <Route path="/cal" element={<Cal />} />
          </Routes>
        </Router>
      </ListState>
    </TaskState>
  );
}

export default App;
