import React, { useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background-image: url('https://example.com/background-image.jpg'); /* Replace with your background image URL */
  background-size: cover;
  background-position: center;
  overflow: hidden;
  position: relative;
`;

const Slider = styled.div`
  display: flex;
  flex-direction: row;
  transition: transform 0.5s ease-in-out;
  transform: translateX(${(props) => props.slideIndex * -100}%);
`;

const Section = styled.div`
  width: 100vw;
  flex: 0 0 auto;
  text-align: center;
  padding: 20px;
  background-color: rgba(255, 255, 255, 0.8);
  border-radius: 10px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  margin: 0 10px;
`;

const Heading = styled.h1`
  font-size: 36px;
  margin-bottom: 20px;
  color: white;
`;

const Description = styled.p`
  font-size: 18px;
  margin-bottom: 40px;
  color: white;
  text-align: center;
`;

const AuthLinks = styled.div`
  display: flex;
  justify-content: center;
  margin-top: 20px;
`;

const Button = styled.button`
  padding: 10px 20px;
  font-size: 18px;
  border: none;
  border-radius: 5px;
  background-color: #007bff; /* Customize the button background color */
  color: white;
  cursor: pointer;
  transition: background-color 0.3s;
  text-decoration: none;
  text-align: center;

  &:hover {
    background-color: #0056b3; /* Change the color on hover */
  }
`;

const Default = () => {
  const [slideIndex, setSlideIndex] = useState(0);

  const nextSlide = () => {
    setSlideIndex((prevIndex) => (prevIndex === 1 ? 0 : prevIndex + 1));
  };

  return (
    <Container>
      <Slider slideIndex={slideIndex}>
        <Section>
          <Heading>Task Manager</Heading>
          <Description>
            Manage your tasks efficiently with our easy-to-use Task Manager.
          </Description>
        </Section>

        <Section>
          <h2>Section 2</h2>
          <p>Content for Section 2 goes here.</p>
          <Button onClick={nextSlide}>Next Section</Button>
        </Section>
      </Slider>

      {slideIndex === 0 && (
        <AuthLinks>
          <Button as="a" href="/login">
            Login
          </Button>
          <Button as="a" href="/signup">
            Sign Up
          </Button>
        </AuthLinks>
      )}
    </Container>
  );
};

export default Default;
